import UIKit

class PaymentInteractor: PaymentInteractorInputProtocol {
    
    let service = Provider.shared.paymentAPIService
    
    func getInvoiceByPayment(param: GetInvoiceParam) {
        service.getInvoiceByPayment(param: param, success: { (result) in
            self.presenter?.didGetInvoiceByPayment(result: result, error: nil)
        }) { (error) in
            self.presenter?.didGetInvoiceByPayment(result: nil, error: error)
        }
    }
    
    func insertOrUpdateInvoice(param: InsertInvoiceParam) {
        Provider.shared.receptionistAPIService.insertOrUpdateInvoice(param: param, success: { (result) in
            self.presenter?.didInsertOrUpdateInvoice(result: result, error: nil)
        }) { (error) in
            self.presenter?.didInsertOrUpdateInvoice(result: nil, error: error)
        }
    }
    

    weak var presenter: PaymentInteractorOutputProtocol?
}
