import UIKit

class RoomManagementInteractor: RoomManagementInteractorInputProtocol {
    
    let service = Provider.shared.roomManagementAPIService
    func getFloorWithRoom(token: String) {
        service.getFloorWithRoom(token: token, success: { (result) in
            self.presenter?.didGetFloorWithRoom(result: result, error: nil)
        }) { (error) in
            self.presenter?.didGetFloorWithRoom(result: nil, error: error)
        }
    }
    

    weak var presenter: RoomManagementInteractorOutputProtocol?
}
