import UIKit

class EditServiceInteractor: EditServiceInteractorInputProtocol {
    
    let service = Provider.shared.receptionistAPIService
    
    func addWidget(param: WidgetEntity) {
        service.addWidget(param: param) { (result) in
            self.presenter?.didAddWidget(result: result, error: nil)
        } failure: { (error) in
            self.presenter?.didAddWidget(result: nil, error: error)
        }
    }
    

    weak var presenter: EditServiceInteractorOutputProtocol?
}
